package org.com.programmers;

public class programmers_02 {
    public static void main(String[] args) {
        int n=5;
        String answer = "";
        String a = "수박";
        String b = "수";

        if(n%2 ==0){
            for(int i=0;i< n/2;i++){
                answer =  a.repeat(n/2);
            }
        }else if (n%2 ==1){
            answer = a.repeat(n/2) + b;
        }
        System.out.println(answer);
    }

}
